mapper.select:
select chart_view.*
from chart_view
where id = #{id}

select
chart_view.*
from chart_view where id = #{id}

select
chart_view_cache.*
from chart_view_cache where id = #{id}

select count(DISTINCT table_id)
from chart_view
where id = #{viewIdSource}
   or id = #{viewIdTarget}

mapper.delete:
delete from chart_view_cache
where scene_id = #{panelId}

    and id not in

        #{viewId}

delete from chart_view
where scene_id = #{panelId}

    and id not in

        #{viewId}
