mapper.select:
select id, `name`, ifnull(pid, 0) as pid, `level`, node_type, `table_name`, datasource, create_index, create_by, create_time, update_by, update_time, `name` as label, authInfo.privileges as `privileges` from (select GET_V_AUTH_MODEL_ID_P_USE (#{userId}, 'data_fill') cids) t, data_fill_form left join ( SELECT auth_source, group_concat( DISTINCT sys_auth_detail.privilege_extend ) as `privileges` FROM ( `sys_auth` LEFT JOIN `sys_auth_detail` ON (( `sys_auth`.`id` = `sys_auth_detail`.`auth_id` ))) WHERE sys_auth_detail.privilege_value = 1 AND sys_auth.auth_source_type = 'data_fill' AND ( ( sys_auth.auth_target_type = 'dept' AND sys_auth.auth_target IN ( SELECT dept_id FROM sys_user WHERE user_id = #{userId} ) ) OR ( sys_auth.auth_target_type = 'user' AND sys_auth.auth_target = #{userId} ) OR ( sys_auth.auth_target_type = 'role' AND sys_auth.auth_target IN ( SELECT role_id FROM sys_users_roles WHERE user_id = #{userId} ) ) ) GROUP BY `sys_auth`.`auth_source` ) authInfo on data_fill_form.id = authInfo.auth_source
FIND_IN_SET(data_fill_form.id, cids)
and data_fill_form.id = #{id,jdbcType=VARCHAR}
and data_fill_form.`name` like CONCAT('%', #{name},'%')
and data_fill_form.pid = #{pid,jdbcType=VARCHAR}
and data_fill_form.node_type = #{nodeType,jdbcType=VARCHAR}
and data_fill_form.`level` = #{level,jdbcType=INTEGER}
and data_fill_form.create_by = #{createBy,jdbcType=VARCHAR},
and data_fill_form.create_time = #{createTime,jdbcType=TIMESTAMP},
and data_fill_form.update_by = #{updateBy,jdbcType=VARCHAR},
and data_fill_form.update_time = #{updateTime,jdbcType=TIMESTAMP},
and data_fill_form.id != #{excludedId,jdbcType=VARCHAR}
and id in
#{item}
ORDER BY CONVERT(`name` using gbk)

SELECT t3.id, t3.form_id, t3.data_id, t3.operate, t3.commit_by, t3.commit_time, t4.nick_name FROM (SELECT t1.id, t1.form_id, t1.data_id, t1.operate, t1.commit_by, t1.commit_time FROM (SELECT * FROM data_fill_commit_log WHERE form_id = #{formId}
AND data_id in
#{item}
) t1 INNER JOIN (SELECT MAX(commit_time) AS commit_time, form_id, data_id FROM data_fill_commit_log GROUP BY form_id, data_id) t2 ON t1.form_id = t2.form_id AND t1.commit_time = t2.commit_time AND t1.data_id = t2.data_id) t3 LEFT JOIN (SELECT username, nick_name FROM sys_user) t4 ON t3.commit_by = t4.username

SELECT t1.id, t1.`name`, t1.form_id, t1.start_time, t1.end_time, t1.rate_type, t1.rate_val, t1.publish_start_time, t1.publish_end_time, t1.publish_range_time_type, t1.publish_range_time, t1.creator, t1.create_time, t1.`status`, t1.reci_users, t1.role_list, t1.org_list, t2.nick_name, t3.`name` AS formName FROM (SELECT * FROM data_fill_task WHERE form_id = #{formId}
and `id` = #{id,jdbcType=VARCHAR}
AND `name` LIKE CONCAT('%',#{name},'%')
) t1 LEFT JOIN (SELECT user_id, nick_name FROM sys_user) t2 ON t1.creator = t2.user_id LEFT JOIN (SELECT name, id FROM data_fill_form) t3 ON t1.form_id = t3.id WHERE 1 = 1 ORDER BY t1.create_time DESC
