mapper.select:
SELECT count(1)
FROM visualization_template_category_map cm
WHERE cm.category_id = #{categoryId}

SELECT count(1)
FROM visualization_template_category_map cm
where cm.category_id != #{categoryId}
  and cm.template_id =#{templateId}

SELECT count(1) FROM visualization_template vt LEFT JOIN visualization_template_category_map vtcm ON vt.id = vtcm.template_id WHERE vt.NAME in
#{templateName}
AND vtcm.category_id IN
#{categoryId}
and vt.id not in
#{templateId}

mapper.delete:
delete from visualization_template_category_map tcm
tcm.template_id in ( select id from visualization_template vt where vt.name = #{templateName})
and tcm.template_id = #{templateId}

mapper.select:
select category_id
from visualization_template_category_map
where template_id = #{templateId}

SELECT DISTINCT GROUP_CONCAT( DISTINCT vtcm.category_id ORDER BY vtcm.category_id ) AS selected_subjects FROM visualization_template_category_map vtcm WHERE vtcm.template_id in
#{templateId}
GROUP BY vtcm.template_id
